import {useEffect, useState} from "react";
import {getRoomsType} from "../../utils/ApiFunction";
import {FormControl, Input, InputLabel, MenuItem} from "@mui/material";
import Button from "@mui/material/Button";
import Select from "@mui/material/Select";

const RoomTypeSelector = ({handleRoomInputChange, newRoom}) => {
    const [roomTypes, setRoomTypes] = useState([""]);
    const [showNewRoomTypeInput, setShowNewRoomTypeInput] = useState(false);
    const [newRoomType, setNewRoomType] = useState("");

    useEffect(() => {
        getRoomsType().then((data) => {
            setRoomTypes(data);
        })
    }, []);

    const handleNewRoomTypeInputChange = (e) => {
        setNewRoomType(e.target.value);
    }

    const handleAddNewRoomType = () => {
        if(newRoomType !== ""){
            setRoomTypes([...roomTypes,newRoomType])
            setNewRoomType("")
            setShowNewRoomTypeInput(false)
        }
    }

    return (
        <>
            {roomTypes.length >0 && (
                <div>
                    <FormControl fullWidth>
                        <InputLabel id="roomType" >Select Room Type ...</InputLabel>
                        <Select name="roomType"
                                id="roomType"
                                value={newRoom.roomType}
                                onChange={(e) => {
                                    if(e.target.value ==="Add new") {
                                        setShowNewRoomTypeInput(true)
                                    }else {
                                        handleRoomInputChange(e);
                                    }
                                }}
                        >
                            <MenuItem value="">Select a room type...</MenuItem>
                            {roomTypes.map((type,index) => (
                                <option key={index} value={type}>{type}</option>
                            ))}
                            <option value="Add new">Add new</option>
                        </Select>
                    </FormControl>
                    {showNewRoomTypeInput && (
                        <div className="mt-2">
                            <FormControl>
                                <input
                                    type="text"
                                       placeholder="Enter new room type"
                                       value={newRoomType}
                                       onChange={handleNewRoomTypeInputChange}
                                />
                                <button className="btn btn-hotel" type="button" onClick={handleAddNewRoomType}>Add</button>
                            </FormControl>
                        </div>
                    )}
                </div>
            )}
        </>
    );
};

export default RoomTypeSelector;
